// Get the input data from the message
const entity = msg.payload;
const thresholds = flow.get('thresholds') || {};

// Extract plant type/species
const plantType = entity.species?.value || 'default';

// Initialize results object
let alerts = [];

// Check if we have thresholds for this plant type, otherwise use default
const plantThresholds = thresholds.plants[plantType] || thresholds.plants.default;

// Check temperature thresholds if temperature exists
if (entity.temperature && plantThresholds.temperature) {
    const temp = parseFloat(entity.temperature.value);
    const tempThresholds = plantThresholds.temperature;

    if (temp < tempThresholds.criticalMin) {
        alerts.push({
            type: 'temperature',
            severity: 'critical',
            message: `Critical low temperature detected: ${temp}${tempThresholds.unit}`,
            value: temp,
            threshold: tempThresholds.criticalMin,
            unit: tempThresholds.unit
        });
    } else if (temp < tempThresholds.min) {
        alerts.push({
            type: 'temperature',
            severity: 'warning',
            message: `Low temperature detected: ${temp}${tempThresholds.unit}`,
            value: temp,
            threshold: tempThresholds.min,
            unit: tempThresholds.unit
        });
    } else if (temp > tempThresholds.criticalMax) {
        alerts.push({
            type: 'temperature',
            severity: 'critical',
            message: `Critical high temperature detected: ${temp}${tempThresholds.unit}`,
            value: temp,
            threshold: tempThresholds.criticalMax,
            unit: tempThresholds.unit
        });
    } else if (temp > tempThresholds.max) {
        alerts.push({
            type: 'temperature',
            severity: 'warning',
            message: `High temperature detected: ${temp}${tempThresholds.unit}`,
            value: temp,
            threshold: tempThresholds.max,
            unit: tempThresholds.unit
        });
    }
}

// Check humidity thresholds if humidity exists
if (entity.humidity && plantThresholds.humidity) {
    const humidity = parseFloat(entity.humidity.value);
    const humidityThresholds = plantThresholds.humidity;

    if (humidity < humidityThresholds.criticalMin) {
        alerts.push({
            type: 'humidity',
            severity: 'critical',
            message: `Critical low humidity detected: ${humidity}${humidityThresholds.unit}`,
            value: humidity,
            threshold: humidityThresholds.criticalMin,
            unit: humidityThresholds.unit
        });
    } else if (humidity < humidityThresholds.min) {
        alerts.push({
            type: 'humidity',
            severity: 'warning',
            message: `Low humidity detected: ${humidity}${humidityThresholds.unit}`,
            value: humidity,
            threshold: humidityThresholds.min,
            unit: humidityThresholds.unit
        });
    } else if (humidity > humidityThresholds.criticalMax) {
        alerts.push({
            type: 'humidity',
            severity: 'critical',
            message: `Critical high humidity detected: ${humidity}${humidityThresholds.unit}`,
            value: humidity,
            threshold: humidityThresholds.criticalMax,
            unit: humidityThresholds.unit
        });
    } else if (humidity > humidityThresholds.max) {
        alerts.push({
            type: 'humidity',
            severity: 'warning',
            message: `High humidity detected: ${humidity}${humidityThresholds.unit}`,
            value: humidity,
            threshold: humidityThresholds.max,
            unit: humidityThresholds.unit
        });
    }
}

// Check light thresholds if light exists
if (entity.lightIntensity && plantThresholds.light) {
    const light = parseFloat(entity.lightIntensity.value);
    const lightThresholds = plantThresholds.light;

    if (light < lightThresholds.criticalMin) {
        alerts.push({
            type: 'light',
            severity: 'critical',
            message: `Critical low light detected: ${light}${lightThresholds.unit}`,
            value: light,
            threshold: lightThresholds.criticalMin,
            unit: lightThresholds.unit
        });
    } else if (light < lightThresholds.min) {
        alerts.push({
            type: 'light',
            severity: 'warning',
            message: `Low light detected: ${light}${lightThresholds.unit}`,
            value: light,
            threshold: lightThresholds.min,
            unit: lightThresholds.unit
        });
    } else if (light > lightThresholds.criticalMax) {
        alerts.push({
            type: 'light',
            severity: 'critical',
            message: `Critical high light detected: ${light}${lightThresholds.unit}`,
            value: light,
            threshold: lightThresholds.criticalMax,
            unit: lightThresholds.unit
        });
    } else if (light > lightThresholds.max) {
        alerts.push({
            type: 'light',
            severity: 'warning',
            message: `High light detected: ${light}${lightThresholds.unit}`,
            value: light,
            threshold: lightThresholds.max,
            unit: lightThresholds.unit
        });
    }
}

// Check soil moisture thresholds if soil moisture exists
if (entity.soilMoisture && plantThresholds.soilMoisture) {
    const moisture = parseFloat(entity.soilMoisture.value);
    const moistureThresholds = plantThresholds.soilMoisture;

    if (moisture < moistureThresholds.criticalMin) {
        alerts.push({
            type: 'soilMoisture',
            severity: 'critical',
            message: `Critical low soil moisture detected: ${moisture}${moistureThresholds.unit}`,
            value: moisture,
            threshold: moistureThresholds.criticalMin,
            unit: moistureThresholds.unit
        });
    } else if (moisture < moistureThresholds.min) {
        alerts.push({
            type: 'soilMoisture',
            severity: 'warning',
            message: `Low soil moisture detected: ${moisture}${moistureThresholds.unit}`,
            value: moisture,
            threshold: moistureThresholds.min,
            unit: moistureThresholds.unit
        });
    } else if (moisture > moistureThresholds.criticalMax) {
        alerts.push({
            type: 'soilMoisture',
            severity: 'critical',
            message: `Critical high soil moisture detected: ${moisture}${moistureThresholds.unit}`,
            value: moisture,
            threshold: moistureThresholds.criticalMax,
            unit: moistureThresholds.unit
        });
    } else if (moisture > moistureThresholds.max) {
        alerts.push({
            type: 'soilMoisture',
            severity: 'warning',
            message: `High soil moisture detected: ${moisture}${moistureThresholds.unit}`,
            value: moisture,
            threshold: moistureThresholds.max,
            unit: moistureThresholds.unit
        });
    }
}

// If there are alerts, send them, otherwise stop the flow
if (alerts.length > 0) {
    msg.payload = {
        id: entity.id,
        name: entity.name?.value || entity.id,
        timestamp: new Date().toISOString(),
        alerts: alerts
    };
    return msg;
} else {
    return null; // No alerts, so stop the flow here
}

// Get the alerts from the message
const alertData = msg.payload;

// Check if there are critical alerts
const criticalAlerts = alertData.alerts.filter(alert => alert.severity === 'critical');
const warningAlerts = alertData.alerts.filter(alert => alert.severity === 'warning');

// Prepare outputs
const outputs = [];

// Output 1: Critical alerts (for immediate notification)
if (criticalAlerts.length > 0) {
    outputs[0] = {
        payload: {
            ...alertData,
            alerts: criticalAlerts,
            severity: 'critical'
        }
    };
} else {
    outputs[0] = null;
}

// Output 2: Warning alerts (for dashboard and logging)
if (warningAlerts.length > 0) {
    outputs[1] = {
        payload: {
            ...alertData,
            alerts: warningAlerts,
            severity: 'warning'
        }
    };
} else {
    outputs[1] = null;
}

// Output 3: All alerts (for dashboard and history)
outputs[2] = {
    payload: alertData
};

return outputs;

// Skip if no message
if (!msg.payload) return null;

// Extract alert data
const data = msg.payload;
const plantName = data.name;
const alerts = data.alerts;

// Create notification message
let message = `ðŸš¨ CRITICAL ALERT for ${plantName}:\n`;

// Add each alert to the message
alerts.forEach(alert => {
    message += `- ${alert.message}\n`;
});

// Add timestamp
const timestamp = new Date().toLocaleString();
message += `\nTime: ${timestamp}`;

// Create the message object
msg.payload = message;

return msg;

// Skip if no message
if (!msg.payload) return null;

// Extract alert data
const data = msg.payload;
const plantName = data.name;
const alerts = data.alerts;

// Create notification message
let message = `âš ï¸ WARNING for ${plantName}:\n`;

// Add each alert to the message
alerts.forEach(alert => {
    message += `- ${alert.message}\n`;
});

// Add timestamp
const timestamp = new Date().toLocaleString();
message += `\nTime: ${timestamp}`;

// Create the message object
msg.payload = message;

return msg;

// Format alert for database storage
const alertData = msg.payload;

// Create document for MongoDB
const document = {
    plantId: alertData.id,
    plantName: alertData.name,
    timestamp: new Date(),
    alerts: alertData.alerts.map(alert => ({
        type: alert.type,
        severity: alert.severity,
        message: alert.message,
        value: alert.value,
        threshold: alert.threshold,
        unit: alert.unit
    }))
};

// Set payload to the document
msg.payload = document;

// Set MongoDB collection
msg.collection = 'alert_history';

return msg;

// Forward alerts to dashboard
const alertData = msg.payload;

// Format for dashboard
const dashboardUpdate = {
    plantId: alertData.id,
    plantName: alertData.name,
    timestamp: new Date().toISOString(),
    alerts: alertData.alerts.map(alert => ({
        type: alert.type,
        severity: alert.severity,
        message: alert.message
    }))
};

// Set topic for the dashboard
msg.topic = "plant/alerts";
msg.payload = dashboardUpdate;

return msg;

// This function loads the thresholds from the thresholds.json file
// and stores them in flow context for use by the threshold monitor
const fs = global.get('fs');
const path = global.get('path');

try {
    // Read thresholds file
    const thresholdsPath = path.join(global.get('projectRoot'), 'models', 'thresholds.json');
    const thresholdsContent = fs.readFileSync(thresholdsPath, 'utf8');
    const thresholds = JSON.parse(thresholdsContent);

    // Store in flow context
    flow.set('thresholds', thresholds);

    // Log success
    node.status({fill:"green", shape:"dot", text:"Thresholds loaded"});

    // Return success message
    msg.payload = {
        status: 'success',
        message: 'Thresholds loaded successfully',
        timestamp: new Date().toISOString()
    };
    return msg;
} catch (error) {
    // Log error
    node.status({fill:"red", shape:"ring", text:"Error loading thresholds"});
    node.error("Failed to load thresholds: " + error.message);

    // Return error message
    msg.payload = {
        status: 'error',
        message: 'Failed to load thresholds: ' + error.message,
        timestamp: new Date().toISOString()
    };
    return msg;
}

