// Create subscription request for Orion
const hostUrl = global.get('hostUrl') || 'http://node-red:1880';

msg.payload = {
    "description": "Subscription for plant monitoring alerts",
    "subject": {
        "entities": [
            {
                "idPattern": "Plant:.*",
                "type": "Plant"
            }
        ],
        "condition": {
            "attrs": [
                "temperature",
                "humidity",
                "lightIntensity",
                "soilMoisture"
            ]
        }
    },
    "notification": {
        "http": {
            "url": `${hostUrl}/notifications/plant-alerts`
        },
        "attrs": [
            "id",
            "name",
            "species",
            "temperature",
            "humidity",
            "lightIntensity",
            "soilMoisture"
        ]
    }
};

return msg;
